INSERT INTO `pj_facileforms_forms` VALUES (1,'','','','','','','','',1,NULL,'',0,0,0,0,'text',0,1,1,'','Samples','','','',0,1,0,'SampleContactForm','','','Contact form','This sample is a basic contact form everybody should be able to master. It does not require any scripting skills to make such a form.','','',400,0,450,0,7,1,1,1,1,0,0,0,0,NULL,NULL,0,0,'',2,0,NULL,NULL,1,33,NULL,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,2,0),(2,'','','','','','','','',1,NULL,'',0,0,0,0,'text',0,1,1,'','Samples','','','',1,1,0,'SampleTabNavigation','','','Using tabs for page navigation','Demonstrates how you can build a dialog with tabs for each page. It also shows another reasonable use of rectangles besides framing and highlighting contents.','','',470,0,420,0,3,0,1,0,1,0,0,0,0,NULL,NULL,0,0,'',0,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,2,0),(3,'','','','','','','','',1,NULL,'',0,0,0,0,'text',0,1,1,'','Samples','','','',2,1,0,'SampleCountrySelect','','','Country selection','Shows the interaction between logically connected select lists, as well as dynamic replacement of select list options. Not really stuff for rookies.','','',320,0,240,0,1,0,1,0,1,0,0,0,0,NULL,NULL,0,0,'',0,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,2,0),(4,'','','','','','','','',1,NULL,'',0,0,0,0,'text',0,1,1,'','Samples','','','',3,1,0,'SamplePieceApp','','','Piece sending mail','Shows how a piece can be used to send a mail to an arbitraty address. \r\nIt takes the fields email, subject and message from the form to send a mail.','','',350,0,400,0,1,0,1,0,1,0,0,0,0,NULL,NULL,0,0,'',0,0,NULL,NULL,1,31,NULL,0,NULL,NULL,0,NULL,NULL,2,NULL,'// declare globals used in this piece\r\nglobal $mosConfig_mailfrom, $mosConfig_fromname;\r\n\r\n// include FacileForms standard library\r\n$this->execPieceByName(\'ff_InitLib\');\r\n\r\n// send the mail\r\n$this->sendMail(\r\n    $mosConfig_mailfrom,                // email addr of submitter (1)\r\n    $mosConfig_fromname.\' - Message\',   // name of submitter (1)\r\n    ff_getSubmit(\'email\'),              // email of recipient (2)\r\n    ff_getSubmit(\'subject\'),            // subject of the mail (3)\r\n    ff_getSubmit(\'message\')             // body of the mail (3)\r\n);\r\n\r\n/*--------------------------------------------------------------------\r\nNotes:\r\n\r\n(1) In this sample we use the site admin as submitter of the email,\r\n    but basicly you could use anything. Note that many mail servers \r\n    however impose constraints about the submitters email address and\r\n    in case refuse to sent the mail to prevent from spamming. (This\r\n    will mostly be reported as \'relaying denied\').\r\n\r\n(2) Recipient can also be an array of email addresses if you want to \r\n    send the same mail to multiple recipients. Note that all email\r\n    adresses will however be visible to all recipients, so maybe\r\n    it is better to call $this->sendMail for each recipient \r\n    individually.\r\n\r\n(3) Subject and body could of cause also be composed here in the \r\n    piece instead of taking it completely from the submitted values.\r\n--------------------------------------------------------------------*/',0,NULL,NULL,2,0),(5,'','','','','','','','',1,NULL,'',0,0,0,0,'text',0,1,1,'','Samples','','','',4,1,0,'SampleEmbeddedCode','','','Embedded php code','Demonstrates the use of embedded php code in various ways.','','',180,0,300,0,1,0,1,1,1,0,0,0,0,NULL,NULL,0,0,'',0,2,NULL,'function ff_SampleEmbeddedCode_init()\r\n{\r\n    alert(\r\n        \'This sample shows some applications of embedded php code:\\n\'+\r\n        \'\\n\'+\r\n        \'- Code in static text as personalized welcome\\n\'+\r\n        \'- Options of the select list are populated by a sql query\\n\'+\r\n        \'- A hidden input submitting the user id\\n\'+\r\n        \'\\n\'+\r\n        \'Embedded php code can be used in parameters of forms, elements \'+\r\n        \'and scripts that have a multiline textarea for editing.\'\r\n    );\r\n} // ff_SampleEmbeddedCode_init',1,31,NULL,1,12,NULL,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,2,0),(6,'','','','','','','','',1,NULL,'',0,0,0,0,'text',0,1,1,'','Samples','','','',5,1,0,'SamplePizzaShop','','','Pizza shop','A complete pizza order form with all bells and whistles. Only the selection of pizzas is very limited. And dont blame me if it doesn\'t arrive in time ;)','','',400,0,400,0,6,0,1,1,1,0,0,0,0,NULL,NULL,0,0,'',1,0,NULL,NULL,2,NULL,'function ff_SamplePizzaShop_submitted(status)\r\n{\r\n    if (status != 0) {\r\n        alert(\r\n            \"Sorry, but submission of you entries unfotunately failed.\\n\"+\r\n            \"We will check this problem and hope on your understanding.\\n\"+\r\n            \"You will be redirected to the home page now.\"\r\n        );\r\n        ff_returnHome();\r\n    } else\r\n        ff_switchpage(ff_getPageByName(\'pst_picture\')); // thank you\r\n} // ff_SamplePizzaShop_submitted',0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,2,0),(7,'','','','','','','','',1,NULL,'',0,0,0,0,'text',0,1,1,'','Samples','','','',6,1,0,'RnrContestRegist','','','Rock\'N\'Roll dance contest registration','This is the registration form for the Rock\'N\'Roll dance contest. Every site member can make one registration only in this sample. Existing registrations can be edited.','','',350,0,200,0,2,0,1,1,1,1,0,0,0,NULL,NULL,0,0,'',0,2,NULL,'function ff_RnrContestRegist_init()\r\n{\r\n    if (ff_getElementByName(\'userid\').value==\'0\') ff_switchpage(2);\r\n} // ff_RnrContestRegist_init',0,NULL,NULL,2,NULL,'//+trace dis\r\n\r\n// load the standard form creation utilities\r\n$this->execPieceByName(\'ff_InitLib\');\r\n\r\n// create table if it does not yet exist\r\nff_query(\r\n    \"CREATE TABLE IF NOT EXISTS `#__rnr_contest` (\".\r\n        \"`id` INT( 11 ) NOT NULL AUTO_INCREMENT ,\".\r\n        \"`userid` INT( 11 ) DEFAULT \'0\' NOT NULL ,\".\r\n        \"`couple` VARCHAR( 50 ) NOT NULL ,\".\r\n        \"`age` TINYINT( 1 ) DEFAULT \'0\' NOT NULL ,\".\r\n        \"`acro` TINYINT( 1 ) DEFAULT \'0\' NOT NULL ,\".\r\n        \"`fun` TINYINT( 1 ) DEFAULT \'0\' NOT NULL ,\".\r\n        \"`boogie` TINYINT( 1 ) DEFAULT \'0\' NOT NULL,\".\r\n        \"PRIMARY KEY  (`id`)\".\r\n    \") TYPE=MyISAM AUTO_INCREMENT=1\"\r\n);\r\n\r\n// load old record if any\r\nglobal $record;\r\n$rows = ff_select(\"select * from `#__rnr_contest` where userid=\".JFactory::getUser()->get(\'id\',0));\r\nif (count($rows)) {\r\n   $record = $rows[0];\r\n   ff_setChecked(\'age\', $record->age);\r\n   if ($record->acro  ) ff_setChecked(\'acro\'  ,\'1\');\r\n   if ($record->fun   ) ff_setChecked(\'fun\'   ,\'1\');\r\n   if ($record->boogie) ff_setChecked(\'boogie\',\'1\');\r\n} else\r\n   $record = NULL;',0,NULL,NULL,2,NULL,'//+trace dis\r\n\r\n// load the standard submit utilities\r\n$this->execPieceByName(\'ff_InitLib\');\r\n\r\n// retrieve the submitted values\r\n$recordid = ff_getSubmit(\'recordid\');\r\n$userid   = JFactory::getUser()->get(\'id\',0);\r\n$couple   = ff_getSubmit(\'couple\', \'\');\r\n$age      = ff_getSubmit(\'age\',    0);\r\n$acro     = ff_getSubmit(\'acro\',   0);\r\n$fun      = ff_getSubmit(\'fun\',    0);\r\n$boogie   = ff_getSubmit(\'boogie\', 0);\r\n\r\nif (is_numeric($recordid))\r\n    // update existing record\r\n    ff_query(\r\n        \"update `#__rnr_contest` \".\r\n           \"set `couple` = \'$couple\', \".\r\n               \"`age`    = \'$age\', \".\r\n               \"`acro`   = \'$acro\', \".\r\n               \"`fun`    = \'$fun\', \".\r\n               \"`boogie` = \'$boogie\' \".\r\n         \"where `id`     = $recordid\"\r\n    );\r\nelse\r\n    // save new record\r\n    ff_query(\r\n        \"insert into `#__rnr_contest` \".\r\n            \"(`userid`, `couple`, `age`, `acro`, `fun`, `boogie`) \".\r\n        \"values (\'$userid\', \'$couple\', \'$age\', \'$acro\', \'$fun\', \'$boogie\')\"\r\n    );',2,NULL,'ff_redirectForm(\"RnrContestList\");',2,0),(8,'','','','','','','','',1,NULL,'',0,0,0,0,'text',0,1,1,'','Samples','','','',7,1,0,'RnrContestList','','','Rock\'N\'Roll dance contest partipicant list','Partipicants list for the Rock\'N\'Roll dance contest. \r\n(Please run registration first in order to automaticly create tables)','','',95,1,10,1,1,0,1,1,1,0,0,0,0,NULL,NULL,0,0,'',0,0,NULL,NULL,0,NULL,NULL,1,12,NULL,0,NULL,NULL,2,NULL,'//+trace dis\r\n\r\n// load the standard submit utilities\r\n$this->execPieceByName(\'ff_InitLib\');\r\n\r\n// get the userid to delete\r\n$deleteid = 0;\r\nforeach ($this->submitdata as $data)\r\n    if ($data[_FF_DATA_NAME]==\'deleteid\') {\r\n        $deleteid = $data[_FF_DATA_VALUE];\r\n        break;\r\n    } // if\r\n\r\n// delete the record if userid found\r\nif ($deleteid > 0)\r\n    ff_query(\"delete from `#__rnr_contest` where id=$deleteid\");',0,NULL,NULL,2,450),(9,'','','','','','','','',1,NULL,'',0,0,0,0,'text',0,1,1,'','Samples','','','',8,1,0,'SubmitContent','','','Submit Content','This form shows how content can be submitted by users.','content_outline','',420,0,500,0,3,0,1,1,1,0,0,0,0,NULL,NULL,0,0,'',0,0,NULL,NULL,0,NULL,NULL,2,NULL,'//+trace dis\r\n\r\n// configuration variables\r\n$register = 1;  // 0 = public access, 1 = registred users only\r\n\r\n// load the standard piece library\r\n$this->execPieceByName(\'ff_InitLib\');  \r\n\r\n// check for registred users\r\nif ($register && JFactory::getUser()->get(\'id\',0) == 0) $this->page = 2;',0,NULL,NULL,2,NULL,'//+trace dis\r\n\r\n// configuration variables\r\n$moderate = 1;  // 0 = publish directly, 1 = leave unpublished\r\n\r\n// declare globals used in this piece\r\nglobal $Itemid;\r\n\r\n// load the standard piece library\r\n$this->execPieceByName(\'ff_InitLib\');\r\n\r\n// get values from submission\r\n$name     = ff_getSubmit(\'name\',     \'\');\r\n$title    = ff_getSubmit(\'title\',    \'\');\r\n$text     = ff_getSubmit(\'text\',     \'\');\r\n$section  = ff_getSubmit(\'section\',  0);\r\n$category = ff_getSubmit(\'category\', 0);\r\n\r\n// get the userid of a superadministrator. the article will be created\r\n// by the superadmin, but use as author alias the name of the person\r\n// who submitted it.\r\n$creator = ff_select(\"select id, email, sendEmail from #__users where gid=25\");\r\n$creator = $creator[0]; // take first row\r\n\r\n// format text, date and state\r\n$text = ff_markdown($text);\r\n$date = date( \"Y-m-d H:i:s\" );\r\nif ($moderate) $state = 0; else $state = 1;\r\n\r\n// save new submission into #__content table\r\nff_query(\r\n    \"insert into #__content \".\r\n        \"(title, title_alias, introtext, state, sectionid, \".\r\n         \"catid, created, created_by, created_by_alias) \".\r\n    \"values \".\r\n        \"(\'$title\', \'$title\', \'$text\', \'$state\', \'$section\', \".\r\n         \"\'$category\', \'$date\', \'$creator->id\', \'$name\')\"\r\n);\r\n\r\n// get section and cat name\r\n$section_name = ff_selectValue(\"select title from #__sections where id=\'$section\'\");\r\n$cat_name = ff_selectValue(\"select title from #__categories where id=\'$category\'\");\r\n\r\n// update #__messages table for backend notices\r\n$subject = \"New content submitted\";\r\n$message = \"A new product was submitted by [$name] \".\r\n           \"titled [$title] in section [$section_name] and category [$cat_name]\";\r\nff_query(\r\n    \"insert into #__messages (user_id_from, user_id_to, date_time, subject,message) \".\r\n    \"values (\'\".JFactory::getUser()->get(\'id\',0).\"\', \'$creator->id\', \'$date\', \'$subject\',\'$message\')\"\r\n);\r\n\r\n// send a mail notice to superadmin\r\nif ($creator->sendEmail) {\r\n    // if sent by unregistred we dont know the email\r\n    if (JFactory::getUser()->get(\'id\',0) != 0) $from = JFactory::getUser()->get(\'email\',\'\'); else $from = \"somebody@unknown.com\";\r\n    $this->sendMail(\r\n        $from,              // senders email\r\n        $name,              // senders name\r\n        $creator->email,    // recipient email\r\n        $subject,           // subject line\r\n        $message            // the message\r\n    );\r\n} // if\r\n\r\nff_redirectSelf(\"&ff_page=3\"); // show thank you page',0,NULL,NULL,2,0),(10,'','','','','','','','',1,NULL,'',0,0,0,0,'text',0,1,1,'','Samples','','','',9,1,0,'SubmitContentPreview','','','Submit Content Preview','This is the preview form for the content submit piece. \r\nIt is not meant to be called directly, but by form\r\nSubmitContent.','content_outline','',100,1,10,1,1,0,1,1,1,0,0,0,0,NULL,NULL,0,0,'',0,0,NULL,NULL,0,NULL,NULL,1,12,NULL,0,NULL,NULL,0,NULL,NULL,0,NULL,NULL,2,400),(11,'','','','','','','','',1,NULL,'',0,0,0,0,'text',0,1,1,'','QuickModeForms','','QuickMode','[{\"elements\":[{\"element\":null,\"bfType\":\"Text\",\"elementType\":\"\",\"options\":{\"value\":\"300px\",\"password\":false,\"mailback\":false,\"readonly\":false},\"data1\":\"300px\",\"data2\":\"\",\"data3\":\"\",\"script1cond\":0,\"script1id\":\"1235\",\"script1code\":\"\",\"script1flag1\":0,\"script1flag2\":0,\"script2cond\":0,\"script2id\":\"1228\",\"script2code\":\"\",\"script2flag1\":0,\"script2flag2\":0,\"script2flag3\":0,\"script2flag4\":0,\"script2flag5\":0,\"script3cond\":1,\"script3id\":\"1248\",\"script3code\":\"\",\"script3msg\":\"Please tell us your name.\",\"functionNameScript1\":\"\",\"functionNameScript2\":\"\",\"functionNameScript3\":\"ff_valuenotempty\",\"flag1\":0,\"flag2\":0,\"mailback\":0,\"mailbackfile\":\"\",\"title\":\"Name\",\"name\":\"Name\",\"page\":\"1\",\"orderNumber\":1,\"dbId\":0,\"appElementOrderId\":0,\"id\":0,\"logging\":true,\"qId\":\"bfQuickMode9554026\",\"internalType\":\"bfTextfield\",\"mailbackAsSender\":0,\"tabIndex\":\"-1\",\"hideInMailback\":false},{\"element\":null,\"bfType\":\"Text\",\"elementType\":\"\",\"options\":{\"value\":\"\",\"password\":false,\"mailback\":false,\"readonly\":false},\"data1\":\"\",\"data2\":\"\",\"data3\":\"\",\"script1cond\":0,\"script1id\":\"1235\",\"script1code\":\"\",\"script1flag1\":0,\"s